import re
tabela_dicionário = {}
tabela_de_codons = """
UUU F      CUU L      AUU I      GUU V
UUC F      CUC L      AUC I      GUC V
UUA L      CUA L      AUA I      GUA V
UUG L      CUG L      AUG M      GUG V
UCU S      CCU P      ACU T      GCU A
UCC S      CCC P      ACC T      GCC A
UCA S      CCA P      ACA T      GCA A
UCG S      CCG P      ACG T      GCG A
UAU Y      CAU H      AAU N      GAU D
UAC Y      CAC H      AAC N      GAC D
UAA Stop   CAA Q      AAA K      GAA E
UAG Stop   CAG Q      AAG K      GAG E
UGU C      CGU R      AGU S      GGU G
UGC C      CGC R      AGC S      GGC G
UGA Stop   CGA R      AGA R      GGA G
UGG W      CGG R      AGG R      GGG G
"""
tabela_tratada = re.findall("\w{3} \w+", tabela_de_codons)
for item in tabela_tratada:
    trinca = str(re.findall("\w{3}", item)).strip("['").strip("']")
    if re.match(" Stop$", item) != []:
        ptn = str(re.findall(" Stop$", item)).strip("['").strip("']")
    else:
        ptn = ""
    if re.findall(" \w{1}$", item) != []:
        ptn = str(re.findall(" \w{1}", item)).strip("['").strip("']").lstrip()
    tabela_dicionário[f'{trinca}'] = ptn

sample = ""
codons = re.findall("\w{3}", sample)
for codon in codons:
    if codon in tabela_dicionário.keys():
        print(tabela_dicionário[codon], end="")